package edu;

public class Test1 {

	public static void main(String[] args) {
		{
		// TODO Auto-generated method stub
		System.out.println("이름: 김우정");
		System.out.println("성별: 여자");
		System.out.println("나이: 33세");
		System.out.println("소속: 부산대학교");
		}
		System.out.println("=".repeat(60));
		{
			System.out.print("이름: 김우정\t");
			System.out.print("성별: 여자\t");
			System.out.print("나이: 33세\t");
			System.out.print("소속: 부산대학교");
				// \t는 탭키 삽입
				//\n은 다음줄로 이동
		}
	}

}
